// This is for single-class system only.
for (const actor of game.actors.contents) {
  if (actor.type !== "character") continue;

  await game.fade.registry
    .getSystem("classSystem")
    .onCharacterActorUpdate(actor, {
      system: {
        details: {
          class: "test"
        }
      }
    }, true);
}
