// Prompt for actor name and class
let actorName = await new Promise(resolve => {
    new Dialog({
        title: "Clear Class Data",
        content: `
            <form>
                <div class="form-group">
                    <label>Actor Name</label>
                    <input type="text" id="actor-name" name="actor-name" required>
                </div>
                <div class="form-group">
                    <label>Class Name</label>
                    <input type="text" id="class-name" name="class-name" required>
                </div>
            </form>
        `,
        buttons: {
            ok: {
                label: "Submit",
                callback: html => {
                    const name = html.find("#actor-name").val();
                    const className = html.find("#class-name").val();
                    resolve({ name, className });
                }
            }
        },
        default: "ok",
        close: () => resolve(null)
    }).render(true);
});

// Check if the user provided input
if (actorName) {
    const { name, className } = actorName;

    // Get the actor by name
    const actor = game.actors.getName(name);
    if (actor) {
        // Call the method to clear class data
        await actor.clearClassData(className);
        ui.notifications.info(`Cleared class data for ${name} - ${className}`);
    } else {
        ui.notifications.error(`Actor "${name}" not found.`);
    }
}
