// JavaScript source code
let results = '<h2>Rolling Ability Scores</h2><p>Method: 2x3d6kh</p>';
let accum = 0;
let rollTotals = []; // Array to store the rolled values
let rolls = [];

for (let i = 0; i < 6; i++) {
   let roll;
   do {
      roll = new Roll('{3d6, 3d6}kh');
      await roll.evaluate();
   } while (roll.total < 6);
   accum += roll.total;
   rolls.push(roll);
   rollTotals.push(roll.total); // Store the rolled value
   let discarded =roll.terms[0].results.find(roll2 => roll2.discarded === true);
   results += `Roll ${i + 1}: ${roll.total} (${discarded.result})<br>`;
}

//console.log(results, rolls.map(roll=>roll.terms[0].results.);

let average = Math.round(accum / 6, 2);
results += `<strong>Average:</strong> ${average}<br>`;

// Generate a unique ID for the button
const buttonId = `apply-ability-scores-${Date.now()}`; // Unique ID based on timestamp
results += `<button id="${buttonId}">Apply to Selected</button>`;

let chatMessage = await ChatMessage.create({
   content: results,
   flavor: 'Ability Score Rolls'
});

// Function to check for the button and add the event listener
const checkForButton = async () => {
   const button = document.getElementById(buttonId);
   if (button) {
      button.addEventListener("click", async () => {
         const actor = canvas.tokens.controlled[0]?.actor; // Get the selected actor
         if (!actor) {
            ui.notifications.warn("Please select an actor.");
            return;
         }

         // Get the ability keys from the actor's system.abilities
         const abilityKeys = Object.keys(actor.system.abilities);

         // Ensure we only apply scores to the first 6 abilities
         for (let i = 0; i < Math.min(rollTotals.length, abilityKeys.length); i++) {
            const abilityKey = abilityKeys[i];
            console.log(`Setting ${actor.name} ${abilityKey} from ${actor.system.abilities[abilityKey].value} to ${rollTotals[i]}.`);
            await actor.update({
               [`system.abilities.${abilityKey}.value`]: rollTotals[i]
            });
         }

         ui.notifications.info("Ability scores applied successfully!");
      });
   } else {
      console.log('Button not found...');
      // If the button is not found, check again after a short delay
      setTimeout(checkForButton, 100); // Check again after 100ms
   }
};

// Start checking for the button
checkForButton();
